public class Switch {

	public static void main(String[] args) {
		int j=2;
		switch(j){
		
		    case 0:
		    	System.out.print("value is 0");
		    	break;
		    case 1:
		    	System.out.print("value is 1");
		    	break;
		    case 2:
		    	System.out.print("value is 2");
		    	break;
		    default:
		    	System.out.print("no value");
		    	
		    	
		    	
		    	
		    	
		    	
		
			
		}
				
		// TODO Auto-generated method stub

	}

}
